package com.songrApp.songr.controller;

import com.songrApp.songr.model.Album;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.*;

import java.util.ArrayList;


@Controller
public class RoutesController {

    @GetMapping("/hello")
    public String greetings(@RequestParam(name = "name", required = false, defaultValue = "world") String name, Model model) {
        model.addAttribute("name", name);
        return "hello";
    }

    @GetMapping("/capitalize/{name}")
    public String capitalize(@PathVariable String name, Model model) {
        model.addAttribute("name", name.toUpperCase());
        return "capitalize";
    }

    @RequestMapping(value = "/", method = RequestMethod.GET)
    public String index(Model model) {
        model.addAttribute("message", "Welcome To Our App");
        return "index";
    }


    @GetMapping("/albums")
    public String getClassInformation(Model model) {
        ArrayList<Album> albums = new ArrayList<>();
        Album album1 = new Album("Tamally Maak", "Amr Diab", 10, 150,"https://img.discogs.com/mrVhxSpktswpKo82TcPqv0vK80U=/fit-in/300x300/filters:strip_icc():format(webp):mode_rgb():quality(40)/discogs-images/R-6944106-1500743368-8340.jpeg.jpg");
        albums.add(album1);
        Album album2 = new Album("Aktar Wahed", "Amr Diab", 9, 200,"https://img.discogs.com/60vmnABbNLDfwLMosvcyjPsa-cE=/fit-in/600x605/filters:strip_icc():format(jpeg):mode_rgb():quality(90)/discogs-images/R-10333312-1495486487-7127.jpeg.jpg");
        albums.add(album2);
        Album album3 = new Album("Allem Alby", "Amr Diab", 8, 300,"data:image/jpeg;base64,");
        albums.add(album3);
        model.addAttribute("album", albums);
        return "albums";
    }


}
